import { useState } from "react";
import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const {
    input: firstName,
    hasError: nameHasError,
    isValid: enteredNameIsValid,
    inputChangeHandler: nameChangeHandler, inputBlurHandler: nameBlurHandler
  } = useInput(value => value.trim() !== '');

  const {
    input: lasName,
    hasError: lastNameHasError,
    isValid: enteredLastNameIsValid,
    inputChangeHandler: lastNameChangeHandler, inputBlurHandler: lastNameBlurHandler
  } = useInput(value => value.trim() !== '');

  const {
    input: email,
    hasError: emailNameHasError,
    isValid: enteredEmailIsValid,
    inputChangeHandler: emailChangeHandler, inputBlurHandler: emailBlurHandler
  } = useInput(value => value.trim() !== '');



  let formIsValid;
  if (enteredNameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  }


  function onSubmitHandler(event) {
    event.preventDefault();


  }

  const nameInputClasses = nameHasError
    ? 'form-control invalid'
    : 'form-control';


  return (
    <form onSubmit={onSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='firstName'>First Name</label>
          <input
            type='text'
            id='firstName'
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
            value={firstName}
          />
          {nameHasError && <p>First name can not be empty</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='lastName'>Last Name</label>
          <input
            type='text'
            id='lastName'
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
            value={lasName}
          />
        </div>
        {lastNameHasError && <p>Last name can not be empty</p>}
      </div>

      <div className='form-control'>
        <label htmlFor='email'>E-Mail Address</label>
        <input
          type='email'
          id='email'
          onChange={onChangeHandler}
          onBlur={onBlurHandler}
          value={userInput}
        />
        {userInput && <p>Please enter a valid email address</p>}
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
