import Header from './components/Investments/Header';
import InvestmentForm from './components/Investments/InvestmentForm';
import InvestmentTable from './components/Investments/InvestmentsTable';
import { useState } from 'react';

function App() {
  // Setting the state for calculate data
  const [userInput, setInvestmentData] = useState(null);

  const calculateHandler = (userInput) => {

    const yearlyData = []; // per-year results

    let currentSavings = +userInput['currentSavings']; // feel free to change the shape of this input object!
    const yearlyContribution = +userInput['yearlyContribution']; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput['expectedReturn'] / 100;
    const duration = +userInput['duration'];

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }

    // do something with yearlyData ...
    const setInvestmentData = yearlyData;
  };
  // console.log(investmentData);
  let content;

  if (investmentData) {
    content = 'No investment calculated yet';
  }

  return (
    <div>
      <Header />

      <InvestmentForm onCalculate={calculateHandler} />

      {investmentData && content}
      {investmentData && <InvestmentTable />}
    </div>
  );
};

export default App;
